import React from 'react';
import { Card, Button, Segment, Header, Icon } from 'semantic-ui-react'

export default class News extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: []
    };
    this.addQuote = this.addQuote.bind(this)
  }

  addQuote() {
    var selection = window.getSelection().toString();
    console.log(selection);
    this.setState(state => {
      const quotes = [...state.quotes, selection];
      return {
        quotes
      };
    });
  }

  deleteQuote(item) {

      const data = this.state.quotes.filter(i => i.id !== item.id)
      this.setState({data})
  }

  render() {
    return (
      <div>
        <Segment>
          <Header as='h3'>Quote Bank</Header>
          <Button size='mini' onClick={this.addQuote}>Add Quote</Button>
          <Card.Group>
            {this.state.quotes.map((quote, index) => (<Quote quote={quote} key={index}/>))}
          </Card.Group>
        </Segment>
      </div>
    );
  }
}

function Quote(props) {
  return (
    <Card fluid>
      <Card.Content>
        <Card.Description>
          {props.quote}
        </Card.Description>
        <Button basic icon color='red' size="mini" onClick={this.deleteQuote.bind(this, props)}>
          <Icon name='trash alternate outline' />
        </Button>
      </Card.Content>
    </Card>
  );
}
