import React from 'react';
import { Card, Button, Segment, Header } from 'semantic-ui-react'

export default class News extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: []
    };
    this.handleAddQuote = this.handleAddQuote.bind(this)
  }

  handleAddQuote() {
    var selection = window.getSelection().toString();
    console.log(selection);
    this.setState(state => {
      const quotes = [...state.quotes, selection];
      return {
        quotes
      };
    });
  }

  render() {
    return (
      <div>
        <Segment>
              <Header as='h3'>Quote Bank</Header>

        <Button onClick={this.handleAddQuote}>Add Quote</Button>
        <Card.Group>
          {this.state.quotes.map(quote => ( <Quote quote={quote} />))}
        </Card.Group>
        </Segment>
      </div>
    );
  }
}

