import React from 'react';
import Upload from './Upload'
import TextEditor from './TextEditor'
import News from './News';
import { Grid, Icon, Menu, Segment } from 'semantic-ui-react'

class Tools extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sampleText: "Upload your audio file and an image of your notes"
    };
    this.handleResultChange = this.handleResultChange.bind(this);
  }

  handleResultChange(data) {
    this.setState({
      sampleText: data
    });
  }

  render() {
    var text = this.state.sampleText; // JSON.parse(this.state.sampleText);


    return (
      <div>
        <Grid columns={2} divided style={{ margin: '10px' }}>
          <Grid.Row stretched>
            <Grid.Column width={11}>
              <Segment style={{ overflow: 'auto', maxHeight: '90vh' }}>{this.state.sampleText}</Segment>
              {/*<TextEditor />  ADD  BACK IN WHEN READY */}
            </Grid.Column>
            <Grid.Column width={5}>
              <Segment>
                <h1>Notes</h1>
                <Upload onDataFetched={this.handleResultChange} />
              </Segment>
              <Segment>
                <h1>Quote Bank</h1>
              </Segment>
              <Segment style={{ overflow: 'auto', maxHeight: '60vh' }}>
                <News />
              </Segment>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div>
    );
  }
}

export default Tools;
