import React from 'react';
import { Card, Button, Segment, Header, Icon } from 'semantic-ui-react'

export default class News extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: []
    };
    this.addQuote = this.addQuote.bind(this)
  }

  addQuote() {
    var selection = window.getSelection().toString();
    console.log(selection);
    this.setState(state => {
      const quotes = [...state.quotes, selection];
      return {
        quotes
      };
    });
  }

  triggerDelete(task, index){
    if(window.confirm("Are you sure you want to delete this task?")){
       let taskList = [...this.state.taskList]
       taskList.splice(index, 1);
       this.setState({taskList: taskList})
    }
  }

  render() {
    return (
      <div>
        <Segment>
          <Header as='h3'>Quote Bank</Header>
          <Button size='mini' onClick={this.addQuote}>Add Quote</Button>
          <Card.Group>
            {this.state.quotes.map(quote => (<Quote quote={quote} key={quote.id}/>))}
          </Card.Group>
        </Segment>
      </div>
    );
  }
}

function Quote(props) {
  return (
    <Card fluid>
