// Import React!
import React from 'react'
import { Form, TextArea } from 'semantic-ui-react'

// Define our app...
class TextEditor extends React.Component {

  onChange = ({ value }) => {
    this.setState({ value })
  }

  // Render the editor.
  render() {
    return (
      <div>
        <Form>
          <TextArea placeholder='Transcript' onChange={this.handleChange.bind(this)} />
        </Form>
      </div>
    )
  }
}

export default TextEditor;
