import React from 'react';
import { Card, Icon, Image, Dropdown } from 'semantic-ui-react'

export default class News extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      order: "", // default (empty string) order is by publication time
      refreshing: true
    };
  }
  // Called after a component is mounted
  componentDidMount() {
    var url = 'https://newsapi.org/v2/everything?q=apple' + this.state.order + '&apiKey=3260f1f5672447929f1fb8f3ec7199f4'
    fetch(url)
      .then(result => result.json())
      .then(result => {
        this.setState({
          articles: result.articles,
          refreshing: false,
        });
      }
      )
  }
  handleClick() {
    console.log("clicked");
  }
  handleRefresh() {
    this.setState(
      {
        refreshing: true
      },
      () => this.fetchNews()
    );
  }

  render() {
    return (
      <div>
        Related News
        {/*
        <Dropdown text='Sort by'>
          <Dropdown.Menu>
            <Dropdown.Item text='Publish Date' active="True" onClick={this.handleClick}/>
            <Dropdown.Item text='Open...' description='ctrl + o' />
            <Dropdown.Item text='Save as...' description='ctrl + s' />
          </Dropdown.Menu>
        </Dropdown>
        */}

        <Card.Group>
          {this.state.articles.map((item) => <Article
            image={item.urlToImage}
            title={item.title}
            source={item.source.name}
            description={item.description}
            date={item.publishedAt}
            url={item.url}
          />)}
        </Card.Group>
      </div>
    );
  }
}

function Article(props) {
  return (
    <Card fluid href={props.url} target="_blank">
      {/*<Image src={props.image} wrapped ui={false} />*/}
      <Card.Content>
        <Card.Header>{props.title}</Card.Header>
        <Card.Meta>{props.source}</Card.Meta>
        <Card.Description>
          {props.description}
        </Card.Description>
      </Card.Content>
      <Card.Content extra>
        <a>
          <Icon name='clock outline icon' />
          {props.date}
        </a>
      </Card.Content>
    </Card>
  );
}
