// Import React!
import React from 'react'
import { Form, TextArea } from 'semantic-ui-react'
import { EditorState, Editor, RichUtils} from 'draft-js'
// import { FontAwesomeIcon } from "react-fontawesome";

// import {
//   faBold,
//   faUnderline,
//   faItalic,
//   faAnchor
// } from "free-solid-svg-icons";

// const inlineStyles = [
//   {
//     label: "bold",
//     style: "BOLD",
//     icon: <FontAwesomeIcon icon={faBold} />
//   },
//   {
//     label: "italic",
//     style: "ITALIC",
//     icon: <FontAwesomeIcon icon={faItalic} />
//   },
//   {
//     label: "Underline",
//     style: "UNDERLINE",
//     icon: <FontAwesomeIcon icon={faUnderline} />
//   }
// ];

class TextEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      editorState: EditorState.createEmpty(),
    };

    this.handleChange = this.handleChange.bind(this);
  }

  updateEditorState(editorState){
    this.setState({editorState});
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleKeyCommand(command, editorState) {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      this.onChange(newState);
      return 'handled';
    }
    return 'not-handled';
  }

  _onBoldClick() {
    this.onChange(RichUtils.toggleInlineStyle(this.state.editorState, 'BOLD'));
  }
  // Render the editor.
  render() {
    return (
      <div className="editor-container">
        <div className="toolbar"></div>
        <button onClick={this._onBoldClick.bind(this)}>Bold</button>
        <Editor 
          placeholder="some text" 
          editorState={this.state.editorState}
          onChange={this.updateEditorState.bind(this)}
          handleKeyCommand={this.handleKeyCommand}
        ></Editor>
        {/* <Form>
          <TextArea style={{ minHeight: 630 }} placeholder='Transcript' value={this.state.value} onChange={this.handleChange}/>
        </Form> */}
      </div>
    )
  }
}

export default TextEditor;
